import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../contexts/CartContext';
import { useAuth } from '../contexts/AuthContext';


const products = [
  { id: 1, name: 'Jeans', price: 29.99, description: 'Comfortable blue jeans', image: 'data:image/jpeg;base64,' },
  { id: 2, name: 'Kurthi', price: 49.99, description: 'Stylish kurthi for all occasions', image: 'data:image/jpeg;base64,' },
  { id: 3, name: 'Pant', price: 19.99, description: 'Formal pants for daily wear', image: 'data:image/jpeg;base64,' },
  { id: 4, name: 'Shoes', price: 19.99, description: 'Running shoes with good grip', image: 'data:image/jpeg;base64,' },
  { id: 5, name: 'Shirt', price: 19.99, description: 'Slim fit casual shirt', image: 'https://encrypted-tbn1.gstatic.com/shopping?q=tbn:ANd9GcRpbb7pg2P_uLIvVYzUNiG_ukJFtdXTz7unBQCUiHLZLgh9d5nmfZyUu3-0coD4EM3ZUxaGSaeSH-dSYjpo_aHnAugXASCIAtuT0tx9GiYEcwqeYvkz1o8l' },
  { id: 6, name: 'T-Shirt', price: 19.99, description: 'Cotton t-shirt in various colors', image: 'data:image/jpeg;base64,' },
];


const Catalog = () => {
  const { addToCart } = useCart();
  const { user } = useAuth();
  const navigate = useNavigate();

  const handleAddToCart = (product) => {
    if (!user) {
      navigate('/register');
      return;
    }
    addToCart(product);
    navigate('/cart');
  };

  return (
    <div className="catalog-container">
      <h2>Catalog</h2>
      <div className="product-list">
        {products.map(product => (
          <div key={product.id} className="product-card">
  <img src={product.image} alt={product.name} className="product-image" />
  <h3>{product.name}</h3>
  <p className="product-description">{product.description}</p>
  <p className="product-price">${product.price.toFixed(2)}</p>
  <button className="btn-primary" onClick={() => handleAddToCart(product)}>Add to Cart</button>
</div>

        ))}
      </div>
    </div>
  );
};

export default Catalog;
